import React, { useEffect, useState } from 'react';
import axios from 'axios';

function Products() {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    axios.get(`${process.env.REACT_APP_API_URL}/products`)
      .then(res => {
        setProducts(res.data);
        setLoading(false);
      })
      .catch(err => {
        setError('Lỗi khi tải dữ liệu sản phẩm');
        setLoading(false);
        console.error('Error fetching products:', err);
      });
  }, []);

  if (loading) return <div className="text-center text-gray-600">Đang tải...</div>;
  if (error) return <div className="text-center text-red-500">{error}</div>;

  return (
    <div>
      <h2 style={{ fontSize: '1.5rem', fontWeight: 'bold', color: '#1F2937', marginBottom: '1.5rem' }}>Danh sách sản phẩm</h2>
      <div className="overflow-x-auto">
        <table style={{ minWidth: '100%', backgroundColor: 'white', border: '1px solid #E5E7EB', borderRadius: '0.5rem', boxShadow: '0 4px 6px -1px rgba(0,0,0,0.1)' }}>
          <thead style={{ backgroundColor: '#F3F4F6' }}>
            <tr>
              <th style={{ padding: '0.75rem 1rem', borderBottom: '1px solid #E5E7EB', textAlign: 'left', color: '#4B5563' }}>ID</th>
              <th style={{ padding: '0.75rem 1rem', borderBottom: '1px solid #E5E7EB', textAlign: 'left', color: '#4B5563' }}>Tên</th>
              <th style={{ padding: '0.75rem 1rem', borderBottom: '1px solid #E5E7EB', textAlign: 'left', color: '#4B5563' }}>Giá</th>
              <th style={{ padding: '0.75rem 1rem', borderBottom: '1px solid #E5E7EB', textAlign: 'left', color: '#4B5563' }}>Kho</th>
            </tr>
          </thead>
          <tbody>
            {products.map(p => (
              <tr key={p.id} className="hover:bg-gray-50">
                <td style={{ padding: '0.75rem 1rem', borderBottom: '1px solid #E5E7EB' }}>{p.id}</td>
                <td style={{ padding: '0.75rem 1rem', borderBottom: '1px solid #E5E7EB' }}>{p.name}</td>
                <td style={{ padding: '0.75rem 1rem', borderBottom: '1px solid #E5E7EB' }}>{p.price}</td>
                <td style={{ padding: '0.75rem 1rem', borderBottom: '1px solid #E5E7EB' }}>{p.stock}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default Products;
